import React, { Component } from "react";
import axios from 'axios';
import './CrudAluno.css';
import Main from "../templates/Main";

const title = "Cadastro de Alunos";

const urlAPI = "http://localhost:5258/api/aluno";
const initialState = {
    aluno: {id:0, ra: '', nome:'', codCurso:0},
    lista: []
} 
/*const Alunos= [
    { 'id': 1, 'ra': 11111, 'nome': 'André', 'codCurso': 21 },
    { 'id': 2, 'ra': 22222, 'nome': 'Giovanna', 'codCurso': 41 },
    { 'id': 3, 'ra': 33333, 'nome': 'Pedro', 'codCurso': 51 },
    { 'id': 4, 'ra': 44444, 'nome': 'Maria', 'codCurso': 33 },
]*/

export default class CrudAluno extends Component{

    state = { ...initialState }

    componentDidMount(){
        axios(urlAPI).then(resp => {
            this.setState({ lista: resp.data })
        })
    }

    limpar(){
        this.setState({ lista: resp.data})
    }

    salvar(){
        const aluno = this.state.aluno;
        aluno.codCurso = Number(aluno.codCurso);
        const metodo = aluno.id ? 'put' : 'post';
const url = aluno.id ? `${urlAPI}/${aluno.id}` : urlAPI;

        axios[metodo](urlAPI, aluno)
                .then(resp => {
                    const lista = this.getListaAtualizada(resp.data)
                    this.setState({ aluno: initialState.aluno, lista})
                })
    }

    getListaAtualizada(aluno, add = true){
        const lista = this.state.lista.filter(a = a.id !== aluno.id);
        if (add) lista.unshift(aluno);
        return lista;
    }

    atualizaCampo(event){
        const aluno = { ... this.state.aluno};
    }

    carregar(aluno) {
        this.setState({ aluno })
        }
        remover(aluno) {
        const url = urlAPI + "/" + aluno.id;
        if (window.confirm("Confirma remoção do aluno: " + aluno.ra)) {
        console.log("entrou no confirm");
        axios['delete'](url, aluno)
        .then(resp => {
        const lista = this.getListaAtualizada(aluno, false)
        this.setState({ aluno: initialState.aluno, lista })
        })
        }
        
        Página
        12
        
        }

    renderForm() {
        return (
        <div className="inclui-container">
        <label> RA: </label>
        <input
        type="text"
        id="ra"
        placeholder="RA do aluno"
        className="form-input"
        name="ra"
        
        value={this.state.aluno.ra}
        
        onChange={ e => this.atualizaCampo(e)}
        />
        <label> Nome: </label>
        <input
        type="text"
        id="nome"
        placeholder="Nome do aluno"
        className="form-input"
        name="nome"
        
        value={this.state.aluno.nome}
        
        onChange={ e => this.atualizaCampo(e)}
        />
        <label> Código do Curso: </label>
        <input
        type="number"
        id="codCurso"
        placeholder="0"

        className="form-input"
        name="codCurso"
        
        value={this.state.aluno.codCurso}
        onChange={ e => this.atualizaCampo(e)}
        />
        <button className="btnSalvar"
        onClick={e => this.salvar(e)} >
        Salvar
        </button>
        <button className="btnCancelar"
        onClick={e => this.limpar(e)} >
        Cancelar
        </button>
        </div>
        )
        }

    renderTable(){
        return(
            <div className="listagem">
                <table className="listaAlunos" id="tblistaAlunos">
                    <thead>
                        <tr className="cabecTabela">
                            <th className="tabTituloRa">ID</th>
                            <th className="tabTituloRa">Ra</th>
                            <th className="tabTituloNome">Nome</th>
                            <th className="tabTituloCurso">Curso</th>
                        </tr>
                    </thead>

                    <tbody>
                        {this.state.lista.map(
                            (aluno) =>
                            <tr>
                                <td>{aluno.id}</td>
                                <td>{aluno.ra}</td>
                                <td>{aluno.nome}</td>
                                <td>{aluno.codCurso}</td>
                                <td>
<button onClick={() => this.carregar(aluno)} >
Altera
</button>
</td>
<td>
<button onClick={() => this.remover(aluno)} >
Remove
</button>
</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        )
    }
    render(){
        return(
            <Main title={title}>
                {this.renderForm()}
                {this.renderTable()}
            </Main>
        )
    }
}
