const urlAPIaluno = "http://localhost:5258/api/aluno";//"http://localhost:7250/api/aluno";
const urlAPIcurso = "http://localhost:5258/api/curso";
const initialState = {
    curso: { id: 0, codCurso: '', nomeCurso: '', periodo: "" },
    listaAluno: [],
    listaCurso: [],
    listaCarometro:[],
}
